// Get CSV content from binary data
const binaryData = $input.first().binary.data;
const csvContent = Buffer.from(binaryData.data, 'base64').toString('utf-8');

// Parse CSV manually
const lines = csvContent.trim().split('\n');
const headers = lines[0].split(',');

// Extract unique combinations
const uniqueCombos = new Set();

for (let i = 1; i < lines.length; i++) {
  const values = lines[i].split(',');
  if (values.length >= 3) {
    const city = values[0].trim();
    const customer = values[1].trim();
    const variety = values[2].trim();

    const combo = `${city}|${customer}|${variety}`;
    uniqueCombos.add(combo);
  }
}

// Convert to array of objects
const combinations = [];
for (const combo of uniqueCombos) {
  const [city, customer_id, apple_variety] = combo.split('|');
  combinations.push({
    city: city,
    customer_id: customer_id,
    apple_variety: apple_variety
  });
}

return combinations.map(combo => ({ json: combo }));

// For each prediction result, find and merge with its original combination
const items = [];

for (let i = 0; i < $input.all().length; i++) {
  const predictionResult = $input.all()[i].json;
  const combination = $('Extract Unique Combinations').all()[i].json;

  items.push({
    json: {
      city: combination.city,
      customer_id: combination.customer_id,
      apple_variety: combination.apple_variety,
      prediction: predictionResult.prediction
    }
  });
}

return items;

// Get all prediction results
const predictions = $input.all();

// Calculate next month info
const monthNames = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];
const nextMonth = monthNames[(new Date().getMonth() + 1) % 12];
const nextMonthYear = (new Date().getMonth() === 11) ? new Date().getFullYear() + 1 : new Date().getFullYear();

// Build the header
let message = `ðŸŽ *Apple Tonnage Predictions*\n\n`;
message += `ðŸ“… *${nextMonth} ${nextMonthYear}*\n\n`;

// Group predictions by city
const predictionsByCity = {};
for (const item of predictions) {
  const data = item.json;

  // Get city from inputs field if available, otherwise from the original combination
  const city = data.inputs?.city || data.city;
  const customer = data.inputs?.customer_id || data.customer_id;
  const variety = data.inputs?.apple_variety || data.apple_variety;
  const tonnage = parseFloat(data.prediction).toFixed(2);

  if (!predictionsByCity[city]) {
    predictionsByCity[city] = [];
  }

  predictionsByCity[city].push({
    customer: customer,
    variety: variety,
    tonnage: tonnage
  });
}

// Format predictions by city
for (const city in predictionsByCity) {
  message += `ðŸ“ *${city}*\n`;

  for (const pred of predictionsByCity[city]) {
    message += `  â€¢ ${pred.customer} - ${pred.variety.charAt(0).toUpperCase() + pred.variety.slice(1)}: *${pred.tonnage} tons*\n`;
  }

  message += `\n`;
}

return {
  message: message
};
