// Get the incoming message
const message = $input.item.json.message;
const chatId = message.chat.id;
const text = message.text.trim().toLowerCase();
const userId = message.from.id;

// Initialize or get existing conversation state from static data
let conversationKey = `conv_${userId}`;
let state = $getWorkflowStaticData('node')[conversationKey] || {
  step: 'start',
  data: {}
};

// Handle commands
if (text === '/start' || text === '/predict') {
  state = {
    step: 'ask_city',
    data: {}
  };
  $getWorkflowStaticData('node')[conversationKey] = state;
  return {
    chatId,
    response: "ðŸŽ *Apple Tonnage Prediction*\n\nLet's get started! I'll need some information.\n\nðŸ“ Which city are you interested in?\n\nType one of: Riyadh, Jeddah, Dammam",
    step: 'ask_city'
  };
}

if (text === '/help') {
  return {
    chatId,
    response: "ðŸ¤– *Available Commands:*\n\n/start - Start new prediction\n/predict - Start new prediction\n/valid - Show valid values\n/cancel - Cancel current prediction\n/help - Show this help",
    step: 'help'
  };
}

if (text === '/cancel') {
  delete $getWorkflowStaticData('node')[conversationKey];
  return {
    chatId,
    response: "âŒ Prediction cancelled. Use /start to begin a new one.",
    step: 'cancelled'
  };
}

if (text === '/valid') {
  return {
    chatId,
    response: "ðŸ“‹ *Valid Values:*\n\n*Cities:* Riyadh, Jeddah, Dammam\n*Customers:* Lulu, Carrefour, Panda\n*Varieties:* fuji, gala, granny_smith\n*Months:* jan, feb, mar, apr, may, jun, jul, aug, sep, oct, nov, dec\n*Years:* 2024, 2025\n\nUse /start to begin a prediction.",
    step: 'valid_values',
    skipPrediction: true
  };
}

// Process based on current step
if (state.step === 'ask_city') {
  const validCities = ['riyadh', 'jeddah', 'dammam'];
  if (validCities.includes(text)) {
    state.data.city = text.charAt(0).toUpperCase() + text.slice(1);
    state.step = 'ask_customer';
    $getWorkflowStaticData('node')[conversationKey] = state;
    return {
      chatId,
      response: `Great! City: *${state.data.city}*\n\nðŸ‘¥ Which customer?\n\nType one of: Lulu, Carrefour, Panda`,
      step: 'ask_customer'
    };
  } else {
    return {
      chatId,
      response: `âŒ Invalid city. Please choose: Riyadh, Jeddah, or Dammam`,
      step: 'ask_city'
    };
  }
}

if (state.step === 'ask_customer') {
  const validCustomers = ['lulu', 'carrefour', 'panda'];
  if (validCustomers.includes(text)) {
    state.data.customer_id = text.charAt(0).toUpperCase() + text.slice(1);
    state.step = 'ask_variety';
    $getWorkflowStaticData('node')[conversationKey] = state;
    return {
      chatId,
      response: `Perfect! Customer: *${state.data.customer_id}*\n\nðŸŽ Which apple variety?\n\nType one of: fuji, gala, granny_smith`,
      step: 'ask_variety'
    };
  } else {
    return {
      chatId,
      response: `âŒ Invalid customer. Please choose: Lulu, Carrefour, or Panda`,
      step: 'ask_customer'
    };
  }
}

if (state.step === 'ask_variety') {
  const validVarieties = ['fuji', 'gala', 'granny_smith'];
  if (validVarieties.includes(text)) {
    state.data.apple_variety = text;
    state.step = 'ask_year';
    $getWorkflowStaticData('node')[conversationKey] = state;
    return {
      chatId,
      response: `Excellent! Variety: *${state.data.apple_variety}*\n\nðŸ“… Which year?\n\nType: 2024 or 2025`,
      step: 'ask_year'
    };
  } else {
    return {
      chatId,
      response: `âŒ Invalid variety. Please choose: fuji, gala, or granny_smith`,
      step: 'ask_variety'
    };
  }
}

if (state.step === 'ask_year') {
  const year = parseInt(text);
  if (year === 2024 || year === 2025) {
    state.data.year = year;
    state.step = 'ask_month';
    $getWorkflowStaticData('node')[conversationKey] = state;
    return {
      chatId,
      response: `Got it! Year: *${state.data.year}*\n\nðŸ“† Which month?\n\nType one of: jan, feb, mar, apr, may, jun, jul, aug, sep, oct, nov, dec`,
      step: 'ask_month'
    };
  } else {
    return {
      chatId,
      response: `âŒ Invalid year. Please type: 2024 or 2025`,
      step: 'ask_year'
    };
  }
}

if (state.step === 'ask_month') {
  const validMonths = ['jan', 'feb', 'mar', 'apr', 'may', 'jun', 'jul', 'aug', 'sep', 'oct', 'nov', 'dec'];
  if (validMonths.includes(text)) {
    state.data.month = text;
    state.step = 'complete';
    // Clear the conversation state
    delete $getWorkflowStaticData('node')[conversationKey];
    return {
      chatId,
      response: `Processing your prediction...\n\nâ³ Please wait...`,
      step: 'complete',
      predictionData: state.data
    };
  } else {
    return {
      chatId,
      response: `âŒ Invalid month. Please type a valid month (jan, feb, mar, etc.)`,
      step: 'ask_month'
    };
  }
}

// Default response
return {
  chatId,
  response: "â“ I didn't understand that. Use /help to see available commands or /start to begin a prediction.",
  step: 'unknown'
};

const chatId = $input.first().json.chatId;
const predictionData = $input.last().json;

if (predictionData.status === 'error') {
  return {
    chatId,
    message: `âŒ *Prediction Error*\n\n${predictionData.message}\n\nPlease make sure the model is trained. Use /start to try again.`
  };
}

const prediction = parseFloat(predictionData.prediction).toFixed(2);
const inputs = predictionData.inputs;

// Determine demand level
let demandEmoji = 'ðŸŸ¢';
let demandLevel = 'Low';
if (prediction > 70) {
  demandEmoji = 'ðŸ”´';
  demandLevel = 'High';
} else if (prediction > 50) {
  demandEmoji = 'ðŸŸ¡';
  demandLevel = 'Medium';
}

const message = `${demandEmoji} *Tonnage Prediction Result*\n\n` +
  `ðŸ“‹ *Input Parameters:*\n` +
  `â€¢ City: ${inputs.city}\n` +
  `â€¢ Customer: ${inputs.customer_id}\n` +
  `â€¢ Variety: ${inputs.apple_variety}\n` +
  `â€¢ Year: ${inputs.year}\n` +
  `â€¢ Month: ${inputs.month}\n\n` +
  `ðŸ“Š *Prediction:*\n` +
  `*${prediction} tons*\n\n` +
  `ðŸ“ˆ Demand Level: *${demandLevel}*\n\n` +
  `ðŸ’¡ Use /start for another prediction`;

return { chatId, message };
